import React from 'react';
import { Link, useEffect, useState } from 'react-router-dom';
import authenticatedFetch from './authenticatedFetch';
import SurveyCards from './SurveyCards';

function Home({ userRole }) {
  const [surveys, setSurveys] = useState([]);
  const isLoggedIn = !!localStorage.getItem('accessToken');

  useEffect(() => {
    const fetchSurveys = async () => {
      const url = userRole === 'admin' 
        ? 'https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/get-surveys'
        : `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/surveys-for-user/${userId}`;

      const response = await authenticatedFetch(url);
      const data = await response.json();
      setSurveys(data);
    };

    fetchSurveys();
  }, [userRole, userId]);

  if(!Array.isArray(surveys)) {
    return <div>Loading...</div>
  }

  return (
    <div>
      <nav className="mb-4">
        {isLoggedIn ? (
          <>
            {userRole === 'admin' && (
              <Link 
                to="/create" 
                className="ml-4 bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
              >
                Create New Survey
              </Link>
            )}
          </>
        ) : (
          <>
            <Link 
              to="/login" 
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Login
            </Link>
            <Link 
              to="/register" 
              className="ml-4 bg-purple-500 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
            >
              Register
            </Link>
          </>
        )}
      </nav>
      <SurveyCards surveys={surveys} />
    </div>
  );
}

export default Home;
