import React, { useState, useEffect } from 'react';
import authenticatedFetch from './authenticatedFetch'; // Adjust the path as necessary

function EditSegment({ segmentId }) { // Assume segmentId is passed as a prop
  const [segment, setSegment] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch the segment data
  useEffect(() => {
    const fetchSegment = async () => {
      try {
        const response = await authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/edit-segment/${segmentId}`);
        const data = await response.json();
        setSegment(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching segment:", error);
        setLoading(false);
      }
    };

    fetchSegment();
  }, [segmentId]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/update-segment/${segmentId}`, {
        method: "PUT",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(segment)
      });

      if (response.ok) {
        alert("Segment updated successfully");
      } else {
        alert("Failed to update segment");
      }
    } catch (error) {
      console.error("Error updating segment:", error);
    }
  };

  const handleChange = (event) => {
    setSegment({ ...segment, [event.target.name]: event.target.value });
  };

  if (loading) return <div>Loading...</div>;
  if (!segment) return <div>No segment data found.</div>;

  return (
    <div>
      <h1>Edit Segment</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Segment Name:
          <input
            type="text"
            name="name"
            value={segment.name || ''}
            onChange={handleChange}
          />
        </label>
        {/* Add other fields as needed */}
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
}

export default EditSegment;
