import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import authenticatedFetch from "./authenticatedFetch";

function EditSegment() {
  const { segmentId } = useParams();
  const navigate = useNavigate();
  const [segment, setSegment] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch the segment data
  useEffect(() => {
    const fetchSegment = async () => {
      try {
        const response = await authenticatedFetch(
          `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/segments/${segmentId}`
        );
        if (response.ok) {
          const data = await response.json();
          setSegment(data);
        } else {
          alert("Error fetching segment data.");
          navigate("/segments"); // Navigate back to segments list on error
        }
      } catch (error) {
        console.error("Error fetching segment:", error);
        navigate("/segments");
      }
      setLoading(false);
    };

    fetchSegment();
  }, [segmentId, navigate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticatedFetch(
        `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/update-segment/${segmentId}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(segment),
        }
      );

      if (response.ok) {
        alert("Segment updated successfully");
        navigate("/segments"); // Navigate back to segments list
      } else {
        alert("Failed to update segment");
      }
    } catch (error) {
      console.error("Error updating segment:", error);
    }
  };

  const handleChange = (event) => {
    // TODO: Implement change logic here for form fields
  };

  if (loading) return <div>Loading...</div>;
  if (!segment) return <div>Segment not found.</div>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-4xl font-bold text-center">Edit Segment</h1>
      <form
        onSubmit={handleSubmit}
        className="max-w-md mx-auto mt-8 bg-white shadow-md rounded-md p-6"
      >
        {/* Name field */}
        <label className="block text-gray-700 font-medium mb-2">
          Name:
          <input
            type="text"
            name="name"
            value={segment.name || ""}
            onChange={handleChange}
            className="block w-full mt-1 border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-blue-600 focus:border-blue-600"
          />
        </label>
        {/* Description field */}
        {/* Criteria fields (similar to SegmentManager) */}

        <button
          type="submit"
          className="block w-full mt-4 bg-blue-600 text-white font-bold rounded-md px-4 py-3 hover:bg-blue-700"
        >
          Update Segment
        </button>
      </form>
    </div>
  );
}

export default EditSegment;
