import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import MyBarChart from "./MyBarChart";
import MyHistogram from "./MyHistogram";
import authenticatedFetch from "./authenticatedFetch";

function SurveyVisualizations() {
  const [visualizationData, setVisualizationData] = useState([]);
  const { surveyId } = useParams();

  useEffect(() => {
    Promise.all([
      authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/survey-responses/${surveyId}/visualize`),
      authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/retrieve-survey/${surveyId}`)
    ])
      .then(async ([response1, response2]) => {
        const data1 = await response1.json();
        const data2 = await response2.json();

        // Transform the data for visualization
        const visualizations = data2.questions.map((question) => {
          const questionResponseData = data1[question.question]; // Array of objects with _id and count properties
          return {
            question: question.question, // The question text
            questionType: question.question_type, // The question type

            data: questionResponseData.map(item => ({
              name: item._id,
              value: item.count
            }))
          };
        });

        setVisualizationData(visualizations);
      })
      .catch(error => console.error("Error fetching data:", error));
  }, [surveyId]);

  const renderVisualization = (visualization, key) => {
    switch (visualization.questionType) {
      case "multiple_choice":
        return <MyBarChart key={key} data={visualization.data} />;
      case "rating_scale":
        return <MyHistogram key={key} data={visualization.data} />;
      // Add other cases as needed
      default:
        return <p key={key}>No visualization available for this question type</p>;
    }
  };

  return (
    <div className="p-4 shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Survey Visualizations</h2>
      {visualizationData.map((visualization, index) =>
        renderVisualization(visualization, index)
      )}
    </div>
  );
}

export default SurveyVisualizations;
