import React, { useState, useEffect } from "react";
import authenticatedFetch from "./authenticatedFetch";

function SegmentManager() {
  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newSegment, setNewSegment] = useState({
    name: "",
    description: "",
    criteria: [
      {
        key: "",
        operator: "includes", // options: includes, excludes, greaterThan, lessThan
        value: "",
      },
    ],
  });

  // Fetch all segments
  useEffect(() => {
    const fetchSegments = async () => {
      try {
        const response = await authenticatedFetch(
          `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/segments`
        );
        const data = await response.json();
        setSegments(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching segments:", error);
        setLoading(false);
      }
    };

    fetchSegments();
  }, []);

  const handleNewSegmentChange = (event) => {
    setNewSegment({ ...newSegment, [event.target.name]: event.target.value });
  };

  const handleChange = (event) => {

    // Get the index of the criteria being updated
    const index = newSegment.criteria.findIndex(x => x.key === event.target.name);

    // Create a new criteria array with updated item
    const updatedCriteria = [
      ...newSegment.criteria.slice(0, index), 
      {
        ...newSegment.criteria[index],
        value: event.target.value
      },
      ...newSegment.criteria.slice(index + 1)
    ];

    // Update the component state 
    setNewSegment({
      ...newSegment, 
      criteria: updatedCriteria
    });

  }

  const addCriteria = () => {
    setNewSegment({
      ...newSegment,
      criteria: [
        ...newSegment.criteria,
        {
          key: "",
          operator: "includes", // options: includes, excludes, greaterThan, lessThan
          value: "",
        },
      ],
    });
  };

  const handleNewSegmentSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticatedFetch(
        `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/create-segment`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newSegment),
        }
      );

      if (response.ok) {
        alert("Segment created successfully");
        // Refresh the segments list
        // ... fetchSegments() can be called here
      } else {
        alert("Failed to create segment");
      }
    } catch (error) {
      console.error("Error creating segment:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div class="container mx-auto p-4">
      <h1 class="text-4xl font-bold text-center text-blue-600">
        Segment Manager
      </h1>

      <form
        onSubmit={handleNewSegmentSubmit}
        class="max-w-md mx-auto mt-8 bg-white shadow-md rounded-md p-6"
      >
        <label class="block text-gray-700 font-medium mb-2">
          New Segment Name:
          <input
            type="text"
            name="name"
            value={newSegment.name}
            onChange={handleNewSegmentChange}
            class="block w-full mt-1 border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-blue-600 focus:border-blue-600"
          />
        </label>
        <label class="block text-gray-700 font-medium mb-2">
          New Segment Description:
          <input
            type="text"
            name="description"
            value={newSegment.description}
            onChange={handleNewSegmentChange}
            class="block w-full mt-1 border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-blue-600 focus:border-blue-600"
          />
        </label>

        {newSegment.criteria.map((item, index) => (
          <div key={index}>
            <select name="key" value={item.key} onChange={handleChange}>
              <option value="city">City</option>
              {/* other options */}
            </select>

            <select
              name="operator"
              value={item.operator}
              onChange={handleChange}
            >
              <option value="includes">Includes</option>
              {/* other operator options */}
            </select>

            <input name="value" value={item.value} onChange={handleChange} />
          </div>
        ))}

        <button onClick={addCriteria}>Add Criteria</button>

        <button
          type="submit"
          class="block w-full mt-4 bg-blue-600 text-white font-bold rounded-md px-4 py-3 hover:bg-blue-700"
        >
          Create Segment
        </button>
      </form>

      <h2 class="text-3xl font-bold text-center text-gray-800 mt-12">
        Existing Segments
      </h2>
      {segments.map((segment) => (
        <div
          key={segment._id}
          class="max-w-md mx-auto mt-8 bg-gray-100 shadow-md rounded-md p-6"
        >
          <h3 class="text-2xl font-medium text-gray-900">{segment.name}</h3>
          <p class="text-gray-700 mt-2">{segment.description}</p>
          {/* Display segment criteria overview */}
          <button
            onClick={() => {
              /* logic to edit this segment */
            }}
            class="block w-full mt-4 bg-yellow-600 text-white font-bold rounded-md px-4 py-3 hover:bg-yellow-700"
          >
            Edit
          </button>
        </div>
      ))}
    </div>
  );
}

export default SegmentManager;
