import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import authenticatedFetch from "./authenticatedFetch";
import BackButton from "./BackButton";

function EditSurvey() {
  const { surveyId } = useParams();
  const [survey, setSurvey] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchSurveyData(surveyId).then(data => setSurvey(data));
  }, [surveyId]);

  const handleSurveyUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await authenticatedFetch(
        `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/update-survey/${surveyId}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(survey)
        }
      );

      if (response.ok) {
        alert('Survey updated successfully.');
        navigate("/manage-surveys");
      } else {
        alert('Failed to update survey.');
      }
    } catch (error) {
      console.error('Error updating survey:', error);
      alert('Error updating survey.');
    }
  };

  const handleInputChange = (e) => {
    setSurvey({ ...survey, [e.target.name]: e.target.value });
  };

  // Function to handle changes in survey questions and options (implementation depends on your data structure)

  if (!survey) {
    return <p>Loading survey data...</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <BackButton />
      <form onSubmit={handleSurveyUpdate}>
        <h2 className="text-3xl font-semibold text-gray-800 my-4">Edit Survey</h2>

        <label>Title:</label>
        <input 
          type="text" 
          name="title" 
          value={survey.title || ''} 
          onChange={handleInputChange}
        />

        <label>Description:</label>
        <textarea 
          name="description" 
          value={survey.description || ''} 
          onChange={handleInputChange}
        />

        {/* Render editable fields for each question */}
        {/* Implementation depends on how your questions are structured */}

        <button
          type="submit"
          className="mt-6 bg-blue-600 hover:bg-blue-800 text-white font-bold py-2 px-6 rounded-lg transition duration-300 ease-in-out"
        >
          Update Survey
        </button>
      </form>
    </div>
  );
}

async function fetchSurveyData(surveyId) {
  const response = await authenticatedFetch(
    `https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/surveys/${surveyId}`
  );
  if (!response.ok) {
    throw new Error('Error fetching survey data');
  }
  return response.json();
}

export default EditSurvey;
