import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import SurveyForm from "./SurveyForm"; // Assuming this is a reusable component for both creating and editing surveys

function EditSurvey() {
  const { surveyId } = useParams();
  const [surveyData, setSurveyData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the survey data for editing
    fetchSurveyData(surveyId).then(data => setSurveyData(data));
  }, [surveyId]);

  const handleSurveySubmit = async (editedData) => {
    try {
      const response = await updateSurvey(surveyId, editedData);
      if (response.ok) {
        alert('Survey updated successfully.');
        navigate("/manage-surveys"); // Redirect after successful submission
      } else {
        alert('Failed to update survey.');
      }
    } catch (error) {
      console.error('Error updating survey:', error);
      alert('Error updating survey.');
    }
  };

  if (!surveyData) {
    return <p>Loading survey data...</p>;
  }

  return (
    <div>
      <h2>Edit Survey</h2>
      <SurveyForm initialData={surveyData} onSubmit={handleSurveySubmit} />
    </div>
  );
}

async function fetchSurveyData(surveyId) {
  const response = await fetch(`https://yourapi.com/surveys/${surveyId}`);
  if (!response.ok) {
    throw new Error('Error fetching survey data');
  }
  return response.json();
}

async function updateSurvey(surveyId, editedData) {
  return fetch(`https://yourapi.com/update-survey/${surveyId}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      // Include authorization token if needed
    },
    body: JSON.stringify(editedData)
  });
}

export default EditSurvey;
