import React, { useState, useEffect } from 'react';
import authenticatedFetch from './authenticatedFetch';

function SegmentManager() {
  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newSegment, setNewSegment] = useState({ name: '', description: '', criteria: {} });

  // Fetch all segments
  useEffect(() => {
    const fetchSegments = async () => {
      try {
        const response = await authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/segments`);
        const data = await response.json();
        setSegments(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching segments:", error);
        setLoading(false);
      }
    };

    fetchSegments();
  }, []);

  const handleNewSegmentChange = (event) => {
    setNewSegment({ ...newSegment, [event.target.name]: event.target.value });
  };

  const handleNewSegmentSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/create-segment`, {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newSegment)
      });

      if (response.ok) {
        alert("Segment created successfully");
        // Refresh the segments list
        // ... fetchSegments() can be called here
      } else {
        alert("Failed to create segment");
      }
    } catch (error) {
      console.error("Error creating segment:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Segment Manager</h1>
      <form onSubmit={handleNewSegmentSubmit}>
        <label>
          New Segment Name:
          <input
            type="text"
            name="name"
            value={newSegment.name}
            onChange={handleNewSegmentChange}
          />
        </label>
        {/* Add other fields for new segment */}
        <button type="submit">Create Segment</button>
      </form>

      <h2>Existing Segments</h2>
      {segments.map((segment) => (
        <div key={segment._id}>
          <h3>{segment.name}</h3>
          <p>{segment.description}</p>
          {/* Display segment criteria overview */}
          <button onClick={() => {/* logic to edit this segment */}}>
            Edit
          </button>
        </div>
      ))}
    </div>
  );
}

export default SegmentManager;
