// In EditSurveyQuestion.js
function EditSurveyQuestion({ question, index, onQuestionChange, onOptionChange, onAddOption, onRemoveOption }) {
    switch (question.question_type) {
      case 'multiple_choice':
        return (
          <div key={index} className="mb-4">
            <input 
              type="text" 
              value={question.question} 
              onChange={(e) => onQuestionChange(index, e.target.value)}
              className="text-input"
            />
            {question.options.map((option, optionIndex) => (
              <div key={optionIndex} className="flex items-center mt-1">
                <input 
                  type="text" 
                  value={option} 
                  onChange={(e) => onOptionChange(index, optionIndex, e.target.value)}
                  className="text-input"
                />
                <button onClick={() => onRemoveOption(index, optionIndex)}>Remove</button>
              </div>
            ))}
            <button onClick={() => onAddOption(index)}>Add Option</button>
          </div>
        );

      case 'rating_scale':
      case 'rating':
        return (
          <div key={index} className="mb-4">
            <input 
              type="text" 
              value={question.question} 
              onChange={(e) => onQuestionChange(index, e.target.value)}
              className="text-input"
            />
            {/* Additional inputs for setting the scale */}
          </div>
        );

      case 'open_ended':
      case 'open_text':
        return (
          <div key={index} className="mb-4">
            <textarea 
              value={question.question} 
              onChange={(e) => onQuestionChange(index, e.target.value)}
              className="textarea-input"
              rows="3"
            ></textarea>
          </div>
        );

      default:
        return <p key={index} className="text-red-500">Unsupported question type: {question.type}</p>;
    }
  }

  export default EditSurveyQuestion;
