import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authenticatedFetch from './authenticatedFetch';

function FilteredSurveysList({ userRole }) {
  const [surveys, setSurveys] = useState([]);
  const [userSegments, setUserSegments] = useState([]);
  const [filteredSurveys, setFilteredSurveys] = useState([]); // For the final list of surveys

  // Fetch surveys
  useEffect(() => {
    const fetchSurveys = async () => {
      const surveyResponse = await authenticatedFetch('https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/get-surveys');
      const surveyData = await surveyResponse.json();
      setSurveys(surveyData);
    };

    fetchSurveys();
  }, []);

  // Fetch user segments if not admin
  useEffect(() => {
    if (userRole !== 'admin') {
      const fetchUserSegments = async () => {
        const segmentResponse = await authenticatedFetch('https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/segments');
        const segmentData = await segmentResponse.json();
        setUserSegments(segmentData);
      };

      fetchUserSegments();
    }
  }, [userRole]);

  // Filter surveys based on user segments
  useEffect(() => {
    if (userRole === 'admin') {
      setFilteredSurveys(surveys);
    } else {
      const filtered = surveys.filter(survey => 
        !survey.segments || survey.segments.some(segment => userSegments.includes(segment)) 
      );
      setFilteredSurveys(filtered);
    }
  }, [surveys, userSegments, userRole]);

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Filtered Surveys</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full table-auto">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2">Title</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {surveys.map((survey) => (
              <tr key={survey._id} className="bg-white border-b">
                <td className="px-4 py-2">{survey.title}</td>
                <td className="px-4 py-2 flex justify-around items-center">
                  <Link
                    to={`/survey/${survey._id}`}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out"
                  >
                    View Survey
                  </Link>
                  {/* Admin-specific actions */}
                  {userRole === "admin" && (
                    <>
                      {/* Add additional buttons for admin actions like Copy Link, View Responses, Delete */}
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default FilteredSurveysList;
