import React, { useState, } from 'react';

function UserEditModal({ user, isOpen, onClose, onSave }) {
    // Initialize profile state outside of any conditions
  const [profile, setProfile] = useState({});

  // Update state when the user prop changes
  useEffect(() => {
    if (user && user.profile) {
      setProfile(user.profile);
    } else {
      setProfile({}); // Reset or set to default values if user or user.profile is null
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleSave = () => {
    onSave(user._id, profile);
    onClose();
  };

  // Early return for when modal should not be rendered
  if (!isOpen) return null;

    return (
      <div className="modal">
        {/* Modal content */}
        <div className="modal-content">
          <span className="close" onClick={onClose}>&times;</span>
          <h2>Edit User Profile</h2>
          {/* Add form fields for each profile attribute */}
          <input
            type="text"
            name="firstName"
            value={profile.firstName || ''}
            onChange={handleInputChange}
          />
            <input
                type="text"
                name="lastName"
                value={profile.lastName || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="city"
                value={profile.city || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="country"
                value={profile.country || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="company"
                value={profile.company || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="industry"
                value={profile.industry || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="interests"
                value={profile.interests || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="products"
                value={profile.products || ''}
                onChange={handleInputChange}
            />
            <input
                type="text"
                name="nps"
                value={profile.nps || ''}
                onChange={handleInputChange}
            />
          <button onClick={handleSave}>Save Changes</button>
        </div>
      </div>
    );
  }

export default UserEditModal;
