import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import MyBarChart from "./MyBarChart"; // Import your chart components
import authenticatedFetch from "./authenticatedFetch";

function SurveyVisualizations() {
  const [aggregatedData, setAggregatedData] = useState([]);
  const { surveyId } = useParams();

  useEffect(() => {
    Promise.all([
      authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/survey-responses/${surveyId}/visualize`),
      authenticatedFetch(`https://vigilant-orbit-v6x6pp4w99636w9v-3000.app.github.dev/retrieve-survey/${surveyId}`)
    ])
      .then(async([response1, response2]) => {
        const data1 = await response1.json();
        const data2 = await response2.json();

        // Merge the question_type from the survey data into the aggregatedData
        const mergedData = data1.aggregatedData.map((item, index) => ({
          ...item,
          questionType: data2.questions[index].question_type
        }));

        setAggregatedData(mergedData);
        console
      })
      .catch((error) =>
        console.error("Error fetching data:", error)
      );
  }, [surveyId]);

  // Function to select and render the appropriate visualization component
  const renderVisualization = (data, questionType, key) => {
    switch (questionType) {
      case "multiple_choice":
        return <MyBarChart key={key} data={data} />;
      case "rating_scale":
        return <MyBarChart key={key} data={data} />; // Assuming you have a LineChart component
      // Add other cases as needed
      default:
        return (
          <p key={key}>No visualization available for this question type</p>
        );
    }
  };

  return (
    <div className="p-4 shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Survey Visualizations</h2>
      {Array.isArray(aggregatedData) &&
        aggregatedData.map((data, index) =>
          renderVisualization(data, data.questionType, index) // Assuming data includes questionType
        )}
    </div>
  );
}

export default SurveyVisualizations;
